import { Component, OnInit } from "@angular/core";
import { ManageSitesService } from "../manage-sites.service";
import { Router, ActivatedRoute, ParamMap } from "@angular/router";
import { ParticipantDetail } from "../../../entity/participantDetail";
import { ToastrService } from "ngx-toastr";
import { Location } from "@angular/common";

@Component({
  selector: "app-participant-details",
  templateUrl: "./participant-details.component.html",
  styleUrls: ["./participant-details.component.scss"],
})
export class ParticipantDetailsComponent implements OnInit {
  partcipantId: string = "";
  participantDetails: ParticipantDetail = new ParticipantDetail();
  particpantDetailsBackup: any[] = [];
  constructor(
    private _location: Location,
    private manageSitesService: ManageSitesService,
    private route: ActivatedRoute,
    private toastr: ToastrService
  ) {}

  ngOnInit() {
    this.route.params.subscribe((params) => {
      if (
        params["partcipantId"] != "" &&
        params["partcipantId"] != "undefined" &&
        params["partcipantId"] != undefined
      )
        this.partcipantId = params["partcipantId"];
      this.listDetails();
    });
  }

  search(filterQuery) {
    let query = filterQuery;
    if (query && query.trim() != "" && query.trim() != undefined) {
      this.participantDetails.consentHistory = this.particpantDetailsBackup.filter(
        function (a) {
          return (
            a.email != null &&
            a.email != undefined &&
            a.email.toLowerCase().includes(query.toLowerCase())
          );
        }
      );
    } else {
      this.participantDetails.consentHistory = this.particpantDetailsBackup;
    }
  }
  listDetails() {
    this.manageSitesService
      .showParticipantsDetails(this.partcipantId)
      .subscribe(
        (data) => {
          console.log(data);
          this.participantDetails = data;
          this.particpantDetailsBackup = JSON.parse(
            JSON.stringify(this.participantDetails)
          );
        },
        (error) => {
          this.toastr.error("There is something wrong.Cant get Details");
          this.participantDetails = new ParticipantDetail();
          this.particpantDetailsBackup = [];
        }
      );
  }

  showpdf(baseData) {
    console.log(baseData);
    var currentTime = Date.now();
    console.log(currentTime);
    const linkSource =
      "data:application/pdf;base64," +
      "\n";
    const downloadLink = document.createElement("a");
    const fileName = "consent_" + currentTime + ".pdf";

    downloadLink.href = linkSource;
    downloadLink.download = fileName;
    downloadLink.click();
  }

  backClicked() {
    this._location.back();
  }
  sendInvitation() {
    //let ids = this.participantDetails.participantRegistrySiteid;
    console.log(this.partcipantId);
    let datas = {
      id: [this.partcipantId],
    };
    console.log(datas);
    this.manageSitesService
      .sendAndResendInvitation(this.participantDetails.siteId, datas)
      .subscribe(
        (data) => {
          this.toastr.success(data.successBean.message);
          this.listDetails();
        },
        (error) => {
          this.toastr.error(error.error.errorBean.userMessage);
        }
      );
  }

  EnableDisableInvitation() {
    let status =
      this.participantDetails.onboardringStatus === "Disabled" ? "1" : "0";
    let datas = {
      id: [this.partcipantId],
      status: status,
    };
    this.manageSitesService
      .enableDisableInvitation(this.participantDetails.siteId, datas)
      .subscribe(
        (data) => {
          this.toastr.success(data.successBean.message);
          this.listDetails();
        },
        (error) => {
          this.toastr.error(error.error.errorBean.userMessage);
        }
      );
  }
}
